import React, {useState, useEffect} from 'react';
import axios from 'axios'
    function PostList(props){
    const {name} = props
    const [posts,setData] = useState([])

    useEffect(() => {
     //   Make the network call
     axios.get('http://localhost:3002/posts')
     .then((response)=>{
        setData(response.data) 
     })
     .catch((error) =>{
         console.log(error)
     })
    })
    console.log(posts)
    function handle(e){
        const newdata={...posts}
        newdata[e.target.title]= e.target.value
        setData(newdata)
        console.log(newdata)
        }
    return (
        <>
         <h2>Posts</h2>
         <form>
                    <fieldset>
                    <label>Title</label>
                    <input onChange={(e)=>handle(e)} id="title" value={posts.title} placeholder="title" type="text"></input>
                    <label>Content</label>
                    <input onChange={(e)=>handle(e)} id="content" value={posts.content} placeholder="content" type="text" ></input>
                    </fieldset>
                    </form>
        {posts.map(post =>(
            <div>

                <h1>{post.title}</h1>
                <p>{post.content}</p>

</div>

        ))}</>
    )
}

export default PostList
