import React, {useState, useEffect} from 'react';
import axios from 'axios'
import AddPost from './addPosts';
    function PostList(props){
    const {name} = props
    const [posts,setData] = useState([])

    useEffect(() => {
     //   Make the network call
     axios.get('http://localhost:3002/posts')
     .then((response)=>{
        setData(response.data) 
     })
     .catch((error) =>{
         console.log(error)
     })
    })
    return (
        <>
       <div className='flex'><a href="" className="bg-blue-600 px-2 py-1 text-white">Create Post</a></div> 
         <h2>Posts</h2>
        {posts.map(post =>(
            <div className='flex'>

                <h1>{post.title}</h1>
                <p>{post.content}</p>
                </div>
                 ))}
                 <div className="my-3">
                     <AddPost />
                 </div>
        </>
    )
}

export default PostList
